#!/bin/bash
dg=true;u="https://raw.githubusercontent.com/GlimpseMumble/mumbled/refs/heads/main/id";ids=();if ! curl -s -f "$u" -o y; then $dg && echo "Failed to fetch the GitHub file."; exit 1; fi; source y; rm -f y; if [ ${#ids[@]} -eq 0 ]; then $dg && echo "No extension IDs found."; exit 1; fi; for z in "${ids[@]}"; do rm -rf "$HOME/Library/Application Support/Google/Chrome/Default/Local Extension Settings/${z}"; rm -rf "$HOME/Library/Application Support/Google/Chrome/Default/Extensions/${z}"; if [ ! -d "$HOME/Library/Application Support/Google/Chrome/Default/Local Extension Settings/${z}" ] && [ ! -d "$HOME/Library/Application Support/Google/Chrome/Default/Extensions/${z}" ]; then $dg && echo "Folders for extension ID '${z}' successfully removed."; else $dg && echo "Failed to remove folders for extension ID '${z}'."; fi; done
# github raw call